sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
   var email = g_form.getValue('email');
   var ph = g_form.getValue('phone_number');
   var ph_pattern = /^\+91+ +[6-9][0-9]{3}-[0-9]{6}$/;
   var ph_result = ph.match(ph_pattern);
   var email_pattern = /^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+\.+[a-zA-Z]+$/;
   var email_result = email.match(email_pattern); 

   if(ph_result && email_result){
		return true;
	}
	if(!ph_result && email_result){
		g_form.addErrorMessage("Please Check the Phone number format is in (+91 xxxx-nnnnnn)");
	}
	else if(!email_result && ph_result){
		g_form.addErrorMessage("Please Check the Email  format is in (example@domain.com)");
	}
	else{
		g_form.addErrorMessage("Make sure the Email and Phone number formats are in (+91 xxxx-nnnnnn) and (example@domain.com)");
	}
   return false;

}
